INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('10a8c6d62f49cdde61baed5892b883c4', '1', '啖情说爱 Appetite for Love', 'http://www.q2002.com/show/45500.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1fudpirbayyj20dz0kz0uh.jpg', '高清中字', 'David Mackay', '泰勒&#31185;尔 / 安德鲁&#87;&#27779;克 / Alley Mills', '剧情 / 爱情 / 儿童', '美国', '高清中字', '豆瓣：3.0 ', '　　贺曼国际电影台自制的情人节电影系列\r\n                                    \r\n                                　　女主角前往家乡小镇为公司战略收购的餐厅进行品牌重新规划，但小镇餐厅老板很固执地对抗大企业不愿意改……并且小餐厅的老板刚好是女主角的高中男友……');
INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('111764631ba2133abab0d18267bf5dc0', '', '事与愿违(浮声六记LIVE版) - 许靖韵2018.08.25', 'http://www.q2002.com/show/45696.html', '2018-08-25', '//img1.c.yinyuetai.com/video/mv/180824/0/-M-69840e0cf4e625dc499af8b383eb3934_640x360.jpg?t=20180823084919', 'YY神曲', '许靖韵', '许靖韵', '神曲 / 音乐 / YY', '大陆中国', 'YY神曲', '豆瓣：8.0 ', '浮声六记&#8212;为浮躁的年代留下纯粹的印记浮生若梦,为欢几何,用音乐的“真”性情寻找生活之“乐”PURE LIVE&#8212;纯粹的生活,纯粹的现场。乐器及演唱全部现场收音,展现唱作人们唱、作俱佳的真实力真现场。浮声六记PURE LIVE第二记为许靖韵精心打造剧中剧K歌房,尽情宣泄失恋无畏好奇无罪,许靖韵首支原创国语作品LIVE首唱。');
INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('116197509c4fb0809a9d46b2e3938c75', '1', '刺杀肯尼迪 JFK', 'http://www.q2002.com/show/6032.html', ' ', 'http://ww4.sinaimg.cn/mw690/005BjCpAgw1f3tne4witcj30b40goju5.jpg', '高清中字', '奥利佛&#26031;通', '凯文&#31185;斯特纳 / 茜茜&#26031;派塞克 / 汤米&#26446;&#29756;斯 / 乔&#20329;西 / 加里&#22885;德曼 / 凯文&#36125;肯 / 杰克&#33713;蒙 / 唐纳德&#33832;瑟兰', '剧情 / 悬疑 / 惊悚 / 传记 / 历史', '美国 / 法国', '高清中字', '豆瓣：8.8 ', '　　肯尼迪的遇刺成为了一桩美国历史上的悬案，电影重新审视了那一段历史，到底杀手是一个普通人，还是整个庞大的政治阴谋，真相和幻象，都在调查中渐渐露出端倪。\r\n                                    \r\n                                　　总统遇刺的两小时内，官方认定的凶手即被逮捕，他就是里&#21704;威&#22885;斯瓦多。然而巧合的是，在随后的一个小时，这个嫌疑犯却火速被 一名流氓杀死。经过调查，官方还是认定里&#21704;威&#22885;斯瓦多为真凶。但是，三年后的一个美国地方检察官吉姆&#21152;里森（凯文&#31185;斯特纳 Kevin Costner 饰），却发现肯尼迪中弹的蹊跷，从而推测当年官方的误判，接下来，他的侦查牵连着一个历史上的惊天阴谋。');
INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('11bd62b86fc11b08e8f0fdfb2dd276f5', '1', '直面真相 At kende sandheden', 'http://www.q2002.com/show/45289.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1fu403r6l3kj20tj14hwp0.jpg', '高清中字', '尼尔斯&#39532;尔姆洛斯', 'Jens Albinus / Sofie Deplace J&#114;gensen / Mette Kolding', '剧情', '丹麦', '高清中字', '豆瓣：3.0 ', '　　根据导演亲生父亲的真实故事改编。1944年12月。纳粹占领下的丹麦。一名因动脉瘤而中风的男孩被送进了医院。丹麦最权威的脑外科专家里查德医生成功地给他进行了手术。四十年后，这名男孩死于肝癌，而元凶竟是当年进行脑部透视时所使用的放射性造影剂。在记者Jon的调查之下，一桩丑闻逐渐露出了水面：里查德医生是在知道该造影剂会致癌的情况下给病人使用的，而医疗当局刻意向病人隐瞒了这一情况。当年曾使用过该造影剂的1100名病人中，几十年后已经有300人死于肝癌。一时间矛头都指向了里查德医生。然而，真相并非如此简单……');
INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('11f5a903394fa3bcc101b44a4531bfa6', '1', '鬼咬鬼', 'http://www.q2002.com/show/4769.html', ' ', 'http://ww2.sinaimg.cn/mw690/005BjCpAgw1f2os0ai0akj30b40go0v9.jpg', '高清国粤语', '宋海星', '朴海日 / 尹宰文 / 孔晓振 / 尹汝贞 / 陈智熙', '剧情 / 喜剧 / 家庭', '韩国', '高清中字', '豆瓣：7.2 ', '　　年逾四十仍一事无成的电影导演仁茂（朴海日 饰）居无定所，只好和年逾古稀的母亲（尹菇贞 饰）住在一起，怎奈家中还有一个碌碌无为的哥哥韩茂（尹宰文 饰），韩茂虚度四十四岁，到现在也没有正经工作。屋漏偏遭连阴雨，不久，已结过三次婚的妹妹美妍（孔孝珍 饰）带着十五岁的女儿（陈智熙 饰）也回到娘家居住。三兄妹秉性各异，人生阅历不尽相同，一起挤在同一个屋檐下过着粗糙凌乱的生活。矛盾从打嘴仗开始，兄妹之间互相讥讽，最后则导致拳脚相向。小侄女很有兴趣地关注着这场兄弟、兄妹大战，最终母亲的一句看似再平静不过的话令兄妹三人安稳地坐到饭桌前。看似波澜不惊，实则暗流涌动......');
INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('141dbe55b308dcd9e50234254811f278', '', '汤摇庄的幽奈同学 ゆらぎ&#12398;幽奈さん', 'http://www.q2002.com/show/44548.html', '2018-08-26', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1ft9xr1m1irj20u0175dnf.jpg', '更新至7集 / 共12集', '许靖韵', '许靖韵', '神曲 / 音乐 / YY', '大陆中国', 'YY神曲', '豆瓣：8.0 ', '浮声六记&#8212;为浮躁的年代留下纯粹的印记浮生若梦,为欢几何,用音乐的“真”性情寻找生活之“乐”PURE LIVE&#8212;纯粹的生活,纯粹的现场。乐器及演唱全部现场收音,展现唱作人们唱、作俱佳的真实力真现场。浮声六记PURE LIVE第二记为许靖韵精心打造剧中剧K歌房,尽情宣泄失恋无畏好奇无罪,许靖韵首支原创国语作品LIVE首唱。');
INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('14d7ae6103eccfb6476de42c0bcdcf07', '1', '美女食人族2喋血金三角', 'http://www.q2002.com/show/45535.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1fuegcp1figj205006kq3m.jpg', '高清中字', '仇星', '晋松 / 周梦 / 李雨阳', '华语 / 爱情 / 喜剧 / 动作 / 普通话 / 杀手 / 女权 / 催泪 / 当代 / 搞笑 / 网络大电影 / 黑帮 / 内地 / 独播 / 夺宝 / 脑洞 / 决斗', '大陆中国', '高清中字', '豆瓣：2.6 ', '尼森奉命卧底潜入梵天王的贩毒组织。身份败露误打误撞进入了摩尔巴部落。伊布拉对尼森动了真情。尼森发现他追寻的毒品“摩诘曼陀罗”正是摩尔巴部落的圣物！桑雅称尼森破坏祭典是他与梵天王的阴谋。此时，梵天王接到了手下的报信，威胁伊布拉交出圣物。桑雅想以圣物换取和平，但伊布拉誓死不从。梵天王恼羞成怒，开始肆意破坏部落。由于伊布拉的失误，桑雅带领族人推翻了伊布拉的统治。就在尼森生死攸关之际，伊布拉孤身涉险前来营救，同时部族手下也来营救尼森。原来桑雅和梵天王早有勾结。 梵天王带领手下再度攻入摩尔巴部落，展开了一场冷兵器与热兵器的对决。');
INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('154197bc35506d1daaaac84b7bb70938', '1', '母亲节 Mother\'s Day', 'http://www.q2002.com/show/11132.html', ' ', 'http://ww3.sinaimg.cn/mw690/005BjCpAjw1f65hmvqx0ij30b90goju6.jpg', '高清中字', '盖瑞&#39532;歇尔', '朱莉娅&#32599;伯茨 / 詹妮弗&#23433;妮斯顿 / 杰森&#33487;戴奇斯 / 凯特&#21704;德森 / 布丽特&#32599;伯森 / 蒂莫西&#22885;利芬特 / 萨拉&#20052;克 / 洛尼&#27931;芙 / 卡梅伦&#22467;斯波西托 / 玛格&#39532;丁戴尔 / 罗伯特&#27966;恩 / 薛&#31859;契尔 / 杰克&#24576;特霍尔 / 阿西', '剧情 / 喜剧', '美国', '高清中字', '豆瓣：6.4 ', '　　少根筋的单亲妈咪珊蒂（詹妮弗&#23433;妮斯顿 饰），独力抚养两个儿子，总期盼着与前夫破镜重圆，没想到他却娶了一个20岁的小萝莉；单亲奶爸布莱德利（杰森苏戴西斯 饰），身兼父母两职辛苦拉拔女儿长大，却不知如何与青春期女儿的相处；八百年没见老妈的大龄女子洁西（凯特&#21704;德森 饰），藏着不欲人知的小秘密，不料远在百里外的老爸老妈却意外来访；单身的事业女强人米兰达（朱莉亚&#32599;伯茨 饰），忙到没时间谈恋爱，却在新书发表会上遇见了生命中唯一的缺憾…每个人总有不能说的秘密，母亲节那天，一场热闹的庆祝派对，将彼此的心重新凝聚在一起。');
INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('15ff16ba7c3524390d156600ab87b10c', '1', '原谅他77次 原&#20182;77次', 'http://www.q2002.com/show/30122.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1fh3pp3wgcuj20bu0go430.jpg', '高清中字', '邱礼涛', '蔡卓妍 / 周柏豪  / 诗雅 / 钟欣潼 / 卢巧音 / 郑希怡 / 雷琛瑜 / 郑诗君 / 陆永 / 林盛斌 / 小肥 / 惠英红 / 吴镇宇 / 郑丹瑞 / 李蕙敏 / 泰臣 / 孙佳君 / 韦罗莎 / 黄秋生', '爱情', '香港', '高清中字', '豆瓣：7.0 ', '　　Eva（蔡卓妍 饰） 与Adam（周柏豪 饰）拍拖十年多，却因一件小事突然分手。莫名被分手的Adam酒后与学生Mandy（ 卫诗雅 饰）发生了关系。可随后Mandy却在Adam家中意外发现了一本Eva所写的77次心碎记录，记事簿里Eva一次次的被伤害纪录历历在目&#65;dam愧疚不已，鼓起勇气在众目睽睽之前向Eva道歉。岂料，Eva却在Adam家中发现了Mandy的物品&#21040;底她可否原谅Adam第78次呢？');
