INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('16cc681362c2edc907f09df4bb839386', '1', '狄仁杰之四大天王', 'http://www.q2002.com/show/45031.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1ftsgbdojybj20u0160129.jpg', '高清国语', '徐克', '赵又廷 / 冯绍峰 / 林更新 / 阮经天 / 刘嘉玲 / 马思纯 / 盛鉴 / 杨一威 / 宝日夫 / 张傲月 / 孙蛟龙 / 汪汐潮 / 铁男 / 李炳雷 / 许明虎', '动作 / 悬疑 / 古装', '中国大陆 / 香港', '高清国语', '豆瓣：6.6 ', '　　狄仁杰（赵又廷 饰）大破神都龙王案，获御赐亢龙锏，并掌管大理寺，使他成为武则天（刘嘉玲 饰）走向权力之路最大的威胁。武则天为了消灭眼中钉，命令尉迟真金（冯绍峰 饰）集结实力强劲的「异人组」，妄图夺取亢龙锏。\r\n                                    \r\n                                　　在医官沙陀忠（林更新 饰）的协助下，狄仁杰既要守护亢龙锏，又要破获神秘奇案，还要面对武则天的步步紧逼，大唐江山陷入了空前的危机之中……');
