INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('5182a06e29a9cf0791161c68924880cf', '1', '北斗神拳：拉奥传 激斗之章', 'http://www.q2002.com/show/45507.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1fudpm51x46j205006k3yp.jpg', '高清中字', '未知', '阿部宽 / 石田百合子 / 宇&#21018;士 / 柴崎幸', '动画 / 剧情 / 日本 / 日语 / 复仇 / 高大上 / 残酷 / 虚拟时空 / 逃亡 / 成长 / 杜比音效 / 片库 / 独播 / 二次元 / 燃', '日本', '高清中字', '豆瓣：2.5 ', '愚蠢的人类被贪婪所驱使，按下了那枚摧毁文明的按钮。浩劫过后，人类世界退回到暴力血腥的原始阶段，而在这混乱的世道中，拉奥所统领的拳王军所向披靡。当初，拉奥作为大师兄，曾和健四郎一同学习北斗神拳。这个有着1800年历史的暗杀拳法，始终奉行着唯一继承人的准则。拉奥虽然实力雄厚，却始终缺乏健四郎的悲悯之心。当后者成为北斗神拳的继承人化身为救世主时，拉奥转而走上更为黑暗的道路。当拳王军的铁蹄已经统治这个国家之时，南斗自由军揭竿而起，并邀请健四郎一同对抗他的宿敌&#8212;拳王拉奥……');
