INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('67466c8c1a348c2dbe6c81b8d8e27a81', '1', '新乌龙院之笑闹江湖2018', 'http://www.q2002.com/show/45534.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1fueg83dft1j20ts15o12z.jpg', '抢先版', '刘十六 / 彭建伟', '汪诺乔 / 郭乐 / 王宇泽', '喜剧 / 动作 / 爱情 / 奇幻 / 古装', '中国大陆', '高清国语', '豆瓣：3.0 ', '　　鲲鹏大陆，是一个无神无妖的地界，小七，托了自己师父的福，穿越过去落在王爷北宫 炎的床顶…… 女主小七意外在古董市场认了一个师父，被师父算计穿越到了鲲鹏大陆，以‘猫身’落 在北宫炎的床顶，后沾了北宫炎的血，恢复人形。起初，北宫炎带着目的性的霸宠小七，慢慢的一颗心为她沉沦。 小七被北宫炎霸占之后，很是排斥，但北宫炎对小七悉心照顾，慢慢的小七感受到了温暖，接受了北宫炎；小七得到的自由越来越多，于是她开赌坊、建酒楼、笼络人才搜集情报、 在鲲鹏大陆混的风生水起；除此之外，南夜国的太子殿下南风辰也对米小七产生了浓厚的兴趣。南风辰一袭红衣倾天下，性格乖张，身后却藏着巨大的阴谋......');
INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('67826934377f2cf86afa2ab06fb1e556', '1', '追光少女', 'http://www.q2002.com/show/45631.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1fuizg8dliuj205006k0t3.jpg', '高清中字', '刘欢', '江泽 / 董钰雯 / 薛诺琪', '华语 / 科幻 / 青春 / 悬疑 / 普通话 / 超自然 / 奇葩 / 当代 / 搞笑 / 寻宝 / 网络大电影 / 内地 / 夺宝 / 脑洞', '大陆中国', '高清中字', '豆瓣：2.6 ', '在古老的东方，流传着“千年女儿香”的传说，少女们在清理沉香的过程中，偷偷将最好的小香块偷藏于胸前，随着时间推移，少女的体香和沉香天然融合，久而久之形成独特“女儿香”。王子要娶新娘，新娘有特别爱好，就是喜欢香水，王子不差钱，出天价私人定制绝版香水以献给新娘以博红颜，香水公司竭尽所能，但沉香品质欠佳听闻只有找到中国传说的”千年女儿香“才能研制出来。\r\n香水公司绞尽脑汁，后在中国古籍中找到线索，只有把“千年沉香”搞到手才行。陈宝儿因尊祖训不得买卖，不以为然。\r\n韩冰草根出身，心比天高但命比纸薄，被雷劈两次幸运逃过劫难。后受尽挫折从&#19997;进化为高富帅。\r\n一出夺宝护香的大戏在中国香城就此展开。');
