INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('6f05b8eecc967b0d6b9de41d3aaaedc2', '1', '魅影缝匠 Phantom Thread', 'http://www.q2002.com/show/39148.html', ' ', 'http://wx2.sinaimg.cn/mw690/80df6fe6gy1fogibravzwj20u018btut.jpg', '高清中字', '保罗&#25176;马斯&#23433;德森', '丹尼尔&#25140;-刘易斯 / 薇姬&#20811;里普斯 / 莱丝利&#26364;维尔 / 卡米拉&#25289;瑟福德 / 吉娜&#40614;基 / 布莱恩&#26684;里森 / 哈里特&#26705;塞姆&#21704;里斯 / 理查德&#26684;拉翰 / 西拉斯&#21345;森 / 朱莉娅&#25140;维斯 / 简&#20329;里', '剧情 / 爱情', '美国', '高清中字', '豆瓣：8.1 ', '　　影片故事设定于上世纪50年代的伦敦，裁缝雷诺兹&#20237;德科克(丹尼尔&#25140;-刘易斯饰)和妹妹希利奥(莱丝利&#26364;维尔饰)乃是当时英国时尚界的标杆。皇室名流、电影明星等都依照“伍德科克之家”的时尚标准打造造型。\r\n                                    \r\n                                　　在伍德科克的生命中，无数女人犹如过往烟云般匆匆而过，但她们恰好为伍德科克提供灵感，并制造丝丝情谊。可是一个年轻，且极具个人魅力的女子阿尔玛(薇姬&#20811;里普斯饰)征服了伍德科克，最终成为他的缪斯和情人。\r\n                                    \r\n                                　　但是他在爱的度量中发现自己精心打磨的人生将被“爱情”破坏。');
INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('6f0962a5e894f1ff894ec354a93a4ba2', '1', '恶魔公子爱上我', 'http://www.q2002.com/show/45873.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1fuvr4lojokj205006kaai.jpg', '高清中字', '李磊', '邱晓东 / 汤畅', '华语 / 爱情 / 喜剧 / 普通话 / 友谊 / 超自然 / 清新 / 当代 / 高大上 / 网络大电影 / 内地 / 玄幻', '大陆中国', '高清中字', '豆瓣：2.6 ', '影片讲述了两位性格迥异的大学生，女主贾想想，法律系，是一位品学兼却恪守陈规的女学生；男主路柯，音乐系，擅长钢琴，长相帅气，阳光自信，热爱摇滚乐毕业季，贾想想面临毕业模拟法庭，和同学们紧张准备中，而路柯也因为要夺冠校园歌手大赛，和乐队加紧排练。争夺教师发生口角，双方在酒吧偶遇，两人同一杯酒一饮而尽，第二天醒来后发现双方身体被互换。面对完全不同的生活环境与朋友，决定要用这次机会败坏对方的名声，让对方出尽洋相，直到贾想想的模拟法庭测试与路柯的表演日子快到了，慌忙的他们只好互相合作，教会对方自己的本领。');
