INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('7d5795c21a0a22cb5d9690d86d1e5ac6', '', '至Q宠物屋：小小世界 第一季', 'http://www.q2002.com/show/45082.html', '2018-08-26', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1fturo4fqhhj209i0daaas.jpg', '更新至25集 / 共26集', 'Dallas Parker', '未知', '搞笑 / 益智 / 亲子 / 冒险', '美国', '更新至25集 / 共26集', '豆瓣：3.5 ', '至Q宠物屋来袭，在那里能看到鱼特先生的迎宾台，获取你的钥匙和欢迎大礼包。当你结束这里的游览返回亲爱的主人身边时，需要用这把钥匙打开众多出口中的一个。接下来，就开始探索这片以宠物为中心的乐园吧！在圣伯纳兹乘坐上山吊椅前往滑雪场，还有摇摇美容店，让小猫给你踩踩背，享受一次按摩，还有萌宠游轮，带你徜徉在萌爪星球的大海上。在这个五彩缤纷的萌宠世界里有很多好玩的呢！');
