INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('8938e0c7f615c3f710cc08587821a702', '1', '笑傲赌界', 'http://www.q2002.com/show/44955.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1ftq94ahg5mj205006k0ta.jpg', '高清中字', '沈金飞', '张竞达 / 马倩倩 / 沈贤宇', '华语 / 爱情 / 喜剧 / 剧情 / 普通话 / 亲情 / 复仇 / 黑色幽默 / 励志 / 大片 / 网络大电影 / 民国时期 / 喜悦 / 黑帮 / 内地 / 赌徒 / 独播', '大陆中国', '高清中字', '豆瓣：2.6 ', '赌界赌神失踪，九宫无主，赌界混乱，一身正义但丝毫不懂赌术的男人&#8212;宝哥，为父报仇，隐姓埋名勇闯九宫，他凭借快速记忆和见招拆招的本领，惊险过了两宫之后被暂时掌管赌界的第八宫宫主龙霸天盯上，龙霸天怀疑宝哥身份陆续派若离和黑衣人监控宝哥，不料若离丧失的记忆随着跟宝哥的相遇逐渐恢复，恢复记忆的若离发现原来掌门并没有失踪，而是被一心想要当赌神的龙霸天控制。龙霸天计谋败露后与宝哥决斗惨败，宝哥的身份也随之揭露，原来他正是当年赌界大乱被掌门送走的儿子&#8212;高兴，也是若离亲梅竹马的恋人。');
