INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('98dea1993e1abc399b4a774a19698a14', '1', '睡美人之终 THE LIMIT OF SLEEPING BEAUTY リミット・オブ・スリ恁豫螗 ビュ恁匹', 'http://www.q2002.com/show/45051.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1ftu1ae18ocj20rs13310m.jpg', '高清中字', '李春阳', '天悦 / 钟承恒', '华语 / 爱情 / 喜剧 / 普通话 / 婚姻 / 亲情 / 爆米花 / 励志 / 清新 / 当代 / 网络大电影 / 喜悦 / 内地', '大陆中国', '高清中字', '豆瓣：2.6 ', '成佳是一名硕士毕业的大龄单身男青年，在母亲的催促下疯狂相亲，相亲的过程可谓是千奇百怪，“笑”果十足。正所谓：只要功夫深，铁棒磨成针，成佳最终凭着自己的真诚抱得美人归。');
INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('990259be74f537d6c208c4abf078853e', '1', '我们与驻在先生的700日战争 ぼくたちと&#22312;さんの700日&#20105;', 'http://www.q2002.com/show/45578.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1fugmy3hufyj20fa0lntax.jpg', '高清中字', '&#26412;连平', '市原隼人 / 佐佐木藏之介 / 麻生久美子 / 加治将树 / 石田卓也 / 竹中直人 / 贺来贤人 / 胁知弘 / 小柳友 / &#28006;智嗣 / 坂井真纪 / 石野真子 / 仓科加奈', '喜剧', '日本', '高清中字', '豆瓣：8.2 ', '　　1979年，平静的枥木乡间生活着一群不甘寂寞的少年。鬼点子超多的真间查理（市原隼人 饰）、好色的不良少年西条（石田卓也 饰）、偏差值为0的孝昭（加治将树  饰）、迷恋天文的井上（贺来贤人 饰）、超级大胃王千叶（肋之弘 饰）、颇有女性风采的后辈吉米（富浦智嗣 饰）和留级大王迁村（小柳友 饰），这七个活宝组成的恶作剧小分队让这个平静得有些沉闷的小镇多了一丝生气。\r\n                                    \r\n                                　　不过他们的好日子很快就结束了，枥木迎来了新的驻警（佐佐木藏之介 饰），古板的警察先生和喧闹自在的少年们似乎注定不能相容，他们之间展开了旷日持久的大作战……');
