INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('9c3da3da139ea0fee6df1c10c6a03a0f', '1', '您一定不要错过', 'http://www.q2002.com/show/45023.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1ftsfzrexkhj20p00xcdjx.jpg', '高清国语', '麦丽丝', '崔永元 / 王心刚 / 王晓棠 / 恩和森 / 斯琴高娃 / 腾格尔 / 艾丽娅', '纪录片', '中国大陆', '高清国语', '豆瓣：5.5 ', '　　大型文献纪录片《您一定不要错过》跟随真诚实在的崔永元领略黑白电影老胶片，看于洋、王晓棠、王心刚、恩和森、朱兰琪琪柯、葛根塔娜、刘世龙、谢飞导演的青春年华，穿越70年找回爷爷奶奶们被遗忘了的岁月。听经典好歌“敖包相会”“蒙古人”“嘎达梅林”“草原晨曲”看天上草原、万马奔腾、醉马豪情，断桥落马，我的黑骏马。解析成吉思汗成长，东归英雄长眠，嘎达梅林永生，额吉的3000孤儿，骆驼祥子和虎妞的爱情。看斯琴高娃、三宝、腾格尔、娜仁花、艾丽娅、涂们、宁才、巴音、巴德玛成为影帝影后，荣获集体演员“金鸡奖”的电影人生。一起告别难忘的八月，到成吉思汗水站饮一口清泉，上天边的老哨卡聆听古老的牧歌&#8943;这么好的故事、这么好的音乐、这么好的电影，您一定不要错过！');
