INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('aad6b2040d6055d781b3f9ee93118c81', '1', '西游三藏前传', 'http://www.q2002.com/show/45103.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1ftvxbv82pnj205k08cjrz.jpg', '高清国语', '刘宇晴', '古亚鹏 / 陈依一 / 那威', '奇幻', '中国', '高清国语', '豆瓣：3.0 ', '江流儿成为唐僧要经历十世投胎，第十世江流儿投胎寺庙，意外放走妖精后下被师傅逐出师门下山。在山下看见人间色彩，参加大唐好声音，遇见了心爱的女子，这一路又由孙悟空暗中保护，师徒四人前世因果，后修成正果西天取经，在西天取经路上又遇见前世的女子。唐僧的前世爱过吗？后来西天取经的女儿国国王是前世所欠下的缘分吗？');
