INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('bcdd9304d637eb5585d6048fbf8998a3', '1', '第四类接触 The Fourth Kind', 'http://www.q2002.com/show/44135.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1fstqzoklbkj20u0166qbq.jpg', '高清中字', '奥拉顿德&#22885;逊山米', '米拉&#20052;沃维奇 / 伊莱亚斯&#31185;泰斯 / 威尔&#24085;顿 / 克里&#32422;翰逊 / 哈基姆&#20975;-卡西姆', '科幻 / 悬疑 / 惊悚 / 恐怖', '美国', '高清中字', '豆瓣：6.7 ', '　　美国阿拉斯加德诺曼小镇，在过去的几十年里当地不断发生离奇事件。许多人神秘失踪，还有很多人被严重的失眠所困扰，自杀案件居高不下。单身母亲阿比盖尔&#27888;勒（米拉&#20052;沃维奇 Milla Jovovich 饰）当年曾经历一场诡异的灾难，在那场灾难中，睡在身边的丈夫神秘死亡。在此之后，阿比盖尔开始研究催眠。当得知诺曼镇发生的事情时，她直觉这与自己的经历有很大关系。经过对几名当事人的催眠，阿比盖尔得知他们都有着遭遇莫名生物的经历，在此期间，她更目睹了当事人杀害全家并自杀的悲惨场面。随着调查的深入，真相渐渐浮出水面，然而最为恐怖的事情也随之发生……\r\n                                    \r\n                                　　本片据传由真实事件改编。');
