INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('bf364251a4270e06254bb9a72596b842', '1', '甜心战士：眼泪 CUTIE HONEY -TEARS-', 'http://www.q2002.com/show/29347.html', ' ', 'http://wx2.sinaimg.cn/mw690/005BjCpAgy1fgnqr6o0t4j30bt0gon0v.jpg', '高清中字', 'A.T. / ヒグチリョウ', '西内玛利亚', '动作 / 科幻', '日本', '高清中字', '豆瓣：4.5 ', '　　原作《甜心战士》曾于1973年在《周刊少年Champion》上连载，东映动画曾将其制作成TV动画在朝日电视台播出了25集。在2004年，庵野秀明曾经将该漫画拍成真人版首次搬上银幕，由佐藤江梨子主演并引起了巨大话题。\r\n                                    \r\n                                　　此次的新作将把背景设定在近未来，世界各地气候异常、未知病毒蔓延导致人口急剧下降，根据某位博士的愿望诞生了一名有着人类感情的人工智能甜心战士。在该作中，运动神经超群的西内玛利亚将首次挑战动作戏份。');
INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('c0d09b76dd9f0ede45e1aa5d24cfceca', '1', '炼狱信使 A Prayer Before Dawn', 'http://www.q2002.com/show/44999.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1ftrbwpjenaj20ty15oq8m.jpg', '高清中字', 'Jean-Stéphane Sauvaire', '乔&#31185;尔 / 维他亚&#28504;斯林加姆 / Panya Yimmumphai / Nicolas Shake / Pornchanok Mabklang / Billy Moore / Somlock Kamsing / Sura Sirmalai / Sakd', '剧情', '英国 / 法国', '高清中字', '豆瓣：3.0 ', '　　在全球最严酷且恶名昭彰、被称为“曼谷希尔顿”的曼谷市郊孔普雷监狱，有十条法则：如果你不遵守，你非常有可能会死；如果你遵守，那你一定会死&#8212;这是比利&#25705;尔亲身经历的真实故事。但是，比尔拒绝妥协不甘枉死狱中，他开始置之死地而后生地学习泰拳，在此过程中找寻兄弟般的友谊，走上令人难以置信的救赎之路。');
