INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('c2e249dcb0cc91f3a824b1bd626d8214', '1', '蚁人2：黄蜂女现身 Ant-Man and the Wasp', 'http://www.q2002.com/show/44552.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1ft9xuho20ij20u018f7dk.jpg', '抢先中字', '佩顿&#37324;德', '保罗&#36335;德 / 伊万杰琳&#33673;莉 / 迈克尔&#20329;纳 / 沃尔顿&#25096;金斯 / 鲍比&#22350;纳瓦尔 / 朱迪&#26684;雷尔 / T.I. / 大卫&#36798;斯马齐连 / 汉娜&#20052;恩-卡门 / 艾比&#33713;德&#24343;特森 / 兰道尔&#26420; / 米歇尔&#33778;佛 / 劳伦斯&#33778;什伯恩 / 迈克尔', '动作 / 科幻 / 冒险', '美国', '抢先中字', '豆瓣：7.6 ', '　　来自漫威电影宇宙的新片《蚁人2：黄蜂女现身》重点展现了两位超级英雄令人惊叹的收缩能力。继《美国队长3》的故事线之后，斯科特&#26391;迎来了自己作为超级英雄和孩子父亲的双重身份。一方面，他在背负着蚁人职责的同时努力过好自己的生活；另一方面，二代黄蜂女霍普&#20961;&#25140;恩和汉克&#30382;姆博士又向他传达了一项紧迫的新任务。斯科特必须再次穿上战衣，与黄蜂女并肩作战，共同破解来自过去的谜题。');
INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('c32d96274b046936697d7e54afa11b6b', '1', '特工008 Непобедимый', 'http://www.q2002.com/show/10790.html', ' ', 'http://ww4.sinaimg.cn/mw690/005BjCpAjw1f5wiqi5roaj30890cfmxt.jpg', '高清中字', 'Oleg Pogodin', '弗拉基米尔&#21494;皮凡采夫 / 谢尔盖&#38463;斯塔霍夫 / Harry Borg', '动作', '俄罗斯', '高清中字', '豆瓣：5.7 ', '　　为了追缉跨国犯罪分子，俄罗斯情报局特工伊戈尔&#20811;列姆涅夫（弗拉基米尔&#21494;皮凡采夫 V. Yepifantsev 饰）来到哥伦比亚，但是行动失败，手下全部丧生。这让伊戈尔备受指责，他百口莫辩，被降职。为了戴罪立功，他接受了俄罗斯石油大亨索尔金的案子，后者被控偷税漏税并企图颠覆政府。伊戈尔去欧洲缉拿知悉索尔金犯罪证据的米哈伊尔&#35874;里克。期间，他结识了身材火辣、身手不凡的女间谍奥拉娃（奥莉加&#27861;捷耶娃 Olga Fadeyeva 饰）。起初，伊戈尔误认为奥拉娃是敌人一伙，幸好同伴提醒，才让对方表明心迹，同仇敌忾。两人密切配合并取得了很好的战绩。某夜，伊戈尔正在淋浴，奥拉娃突然闯入，色诱于他。两人激情缠绵之时，她却用早已藏好的针管插入了他的身体，原来这一切不过是个阴谋……');
INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('c4f7198b785640f84370ed5337e7a7af', '1', '杀破狼&#36138;狼 &#30772;狼&#36010;狼', 'http://www.q2002.com/show/31743.html', ' ', 'http://wx1.sinaimg.cn/mw690/80df6fe6ly1fiq7dvttm1j20bw0gotcu.jpg', '高清国语', '帕特里克&#20241;斯', '瑞恩&#38647;诺兹 / 塞缪尔&#26480;克逊 / 加里&#22885;德曼 / 萨尔玛&#28023;耶克 / 艾洛蒂&#34945; / 乔昆姆&#24503;&#38463;尔梅达 / 克里斯蒂&#31859;切尔 / 约瑟芬&#24503;&#25289;&#27874;美 / 山姆&#21704;兹尔丁 / 巴里&#38463;茨玛 / 艾比&#20399;斯 / 米哈伊尔&#25096;尔沃伊', '喜剧 / 动作 / 犯罪', '美国 / 中国大陆 / 保加利亚 / 荷兰', '高清中字', '豆瓣：7.3 ', '　　达瑞斯（塞缪尔&#26480;克逊 Samuel L. Jackson 饰）曾是一名令业内人士闻风丧胆的超级杀手，如今已经伏法的他屈居在铁窗之后，等待着漫长的刑期到头。达瑞斯的妻子索尼娅（萨尔玛&#28023;耶克 Salma Hayek 饰）同样身陷囹圄，某日，达瑞斯得到了一个意外的机会，能够令索尼娅重获自由，作为交换，他必须出庭作证证明邪恶的罪犯弗拉迪斯拉夫（加里&#22885;德曼 Gary Oldman 饰）有罪。\r\n                                    \r\n                                　　作为护送达瑞斯出庭的负责人，艾米莉亚（艾洛蒂&#34945; Elodie Yung 饰）和她的团队遭到了弗拉迪斯拉夫所派出的佣兵的伏击，全军覆没，幸存的艾米莉亚无奈之下只能找到私人保镖兼前男友迈克尔（瑞安&#38647;诺兹 Ryan Reynolds 饰），委托他贴身保护达瑞斯。');
INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('c53146d88cbe70e9417e50ca8e1a9c15', '1', '与神同行 &#44284; &#44760;', 'http://www.q2002.com/show/38174.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1fnk8le23qcj20rs13lwm1.jpg', '高清中字', '武内英树', '绫濑遥 / 坂口健太郎 / 本田翼 / 北村一辉 / 中尾明庆 / 石桥杏奈 / 柄本明 / 加藤刚', '爱情 / 奇幻', '日本', '高清中字', '豆瓣：7.3 ', '　　绫濑遥（32）和坂口健太郎（25）将共同主演定于18年公开的原创映画「今夜在浪漫剧场」，两人在15年的映画「海街」和16年的映画「高台家的成员」中有出演但是无共演场面，这次是首次共演。影片描绘了坂口饰演的梦想成为电影导演的淳朴青年健司以及小遥饰演的从黑白电影中飞出现身于健司面前的公主美雪之间的爱情故事。');
