INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('c6552ba242551d8f04d2d95e33aaa5fd', '1', '机甲核心', 'http://www.q2002.com/show/45162.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1fty84syo41j205k08cglz.jpg', '高清国语', '周煜壹', '曾志伟 / 江奇霖 / 楚镇', '科幻 / 动作', '中国', '高清国语', '豆瓣：3.0 ', '2069年，人类的超高能耗，使地球的各项能源即将消耗殆尽，各方势力因能源抢夺引发的战争，使地球的环境日益恶化。月球储量丰富的HE－3和同位素的热核反应，是人类解决能源危机的希望。但以KSTAN为首的黑色黎明组织，妄图独占HE－3，打造机甲部队，掌控全人类。于是一场因能源抢夺而引发的战争拉开帷幕。');
