INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('ded83ff30516c5b4c53aa22d80a0c64e', '', '天盛长歌', 'http://www.q2002.com/show/45400.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1fu9qcho592j20u015zdva.jpg', '更新至19集 / 共70集', '沈严 / 刘海波', '陈坤 / 倪妮 / 赵立新 / 倪大红 / 袁弘 / 王鸥 / 白敬亭 / 张晓晨 / 刘敏涛 / 海一天 / 朱锐 / 修庆 / 于明加 / 尹铸胜 / 胡可 / 李菁菁', '剧情 / 爱情 / 古装', '中国大陆', '更新至19集 / 共70集', '豆瓣：3.0 ', '　　楚王宁弈，看似风流散漫的当朝六皇子，内心却背负着惨痛往事。他以天下为棋局，洗雪冤屈、惩治奸佞、整肃朝纲，在腥风血雨的朝堂争斗中步步为营。\r\n                                    \r\n                                　　凤知微，被逐高门之女，不甘屈服于坎坷的命运，女扮男装进入青溟书院，一跃成为无双国士魏知，风云渐起于朝野。\r\n                                    \r\n                                　　一个是城府深藏的亲王，一个 是初露锋芒的官场新秀，两人在风云诡变的朝堂里彼此试探、频频过招，相互排斥却又不自禁相互吸引。\r\n                                    \r\n                                　　而当彼此的心渐渐向对方敞开，邂逅的却是命运彻骨的森凉。\r\n                                    \r\n                                　　一场前朝遗孤案，彻底倾覆了她的世界，也打乱了他一向从容的步伐。对立的血脉、亲友的牺牲，成为隔绝他们的万丈绝壁，爱恨是非，从此永在路中。');
