INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('e5a40938aa8c7ed627923464f69d0ada', '1', ' ', 'http://www.q2002.com/show/22555.html', ' ', 'http://wx1.sinaimg.cn/mw690/005BjCpAgy1fclq0zo1xjj30bm0goq5v.jpg', '高清中字', '曹义锡', '李秉宪 / 姜栋元 / 金宇彬 / 严志媛 / 吴达洙 / 陈庆', '动作 / 犯罪', '韩国', '高清中字', '豆瓣：6.6 ', '　　《MASTER》由《绝密跟踪》导演曹义锡执导，围绕一起韩国建国以来最大规模的诈骗案件展开，讲述智能犯罪搜查队和高智商诈骗团伙之间一场侦查与反侦察的智勇博弈。\r\n                                    \r\n                                　　最近在《局内人》中展现绝佳演技的李秉宪将再演反派，在片中饰演领导完美作案计划并拥有出色口才的OneNetwork 公司陈会长一角，他是这起大规模网络诈骗案件的幕后操控者。而姜栋元饰演追查案件的智能犯罪搜查队队长金宰明，凭借高智商和无懈可击领导能力，与陈会长展开正面对垒。值得一提的是，这将是姜栋元出道以来首次扮演警察角色。此外，新生代人气男星金宇彬将在片中出演陈会长的头号智囊“朴将军”，是一个聪明绝顶，在警察和陈会长之间活动自如游刃有余的角色。');
INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('e5eb3d18f3c7752ade5b301f594d9b0d', '1', '阿尔法：狼伴归途 Alpha', 'http://www.q2002.com/show/45662.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1fuk5084kd2j20rs12wqa5.jpg', '抢先版', '艾尔伯特&#20241;斯', '柯蒂&#26031;密特-麦菲 / 娜塔莎&#36808;尔兹 / 蕾奥娜&#32500;埃拉 / 约翰内斯&#35946;克尔&#32422;翰内森 / 延斯&#36203;尔腾 / 梅赛德斯&#24503;拉泽尔达 / 普里亚&#25289;贾拉特南 / 斯潘塞&#21338;加特 / 帕特里克&#24343;拉纳根 / 迈克尔&#20811;鲁斯-代赫勒 / 马辛&#31185;瓦奇克', '剧情 / 冒险', '美国', '抢先版', '豆瓣：7.1 ', '　　故事发生于两万年前的欧洲北部，地球正处于弱肉强食的冰川时期。心地善良的男主角科达是部落首领的儿子，在一次部落集体狩猎中，科达不慎跌落悬崖，众人以为科达已死，但大难不死的科达被洪水卷走幸存下来，只身流落荒野。\r\n                                    \r\n                                　　途中科达与一匹野狼相遇，给它起名阿尔法，意即狼群首领。科达在与其相处的过程中逐渐成长，学习“狼性”并存有“仁心”。这对人狼搭档在险恶的世界中相互为伴，一起踏上返乡之路，人与狼之间第一次建立了友谊。随后的种种奇遇，甚至改变了人类和世界的未来……\r\n                                    \r\n                                　　');
