INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('e9680a0e7d6c07b2472769df20d74a66', '1', '机甲神七', 'http://www.q2002.com/show/45731.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1fuo69le15qj203c04g3yk.jpg', '高清中字', '陈泳旭', '彭禺厶 / 李灿森 / 张婉儿 / 朱佳希', '华语 / 科幻 / 动作 / 剧情 / 普通话 / 日语 / 亲情 / 大片 / 网络大电影 / 未来 / 机甲 / 燃', '大陆中国', '高清中字', '豆瓣：2.6 ', '日本神秘组织“八岐大蛇”的服部次郎，与前来寻找自己女友乐乐的小宇激烈交战，以便采集小宇的外挂“生物机甲”小七的各项数据，打斗中，小宇的“冰系超能力”把服部次郎带入往事回忆中。\r\n原来，这服部次郎是“八岐大蛇”总头目服部三藏的干儿子，原名叫大宇，这个小宇就是服部次郎的亲弟弟。小宇天生患有一种罕见的病，出生时体温比正常人低七度，随着年龄的增长体温持续下降，情绪激动时体温会急剧降低，甚至把水冻住。后来，中国神秘组织“龙组”找到小宇，并为他设计了机甲“小七”以此维持体温。“八岐大蛇”总部一战后正式加入中国“龙组”，成为一名“机甲特工”。');
INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('e9ed9152dbfb22b511a212db2d746061', '1', '不忘初心2018', 'http://www.q2002.com/show/45156.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1fty7kfuch8j205006kjrs.jpg', '高清中字', '徐老焉', '7 - king / 乐天蜀黍哇', '华语 / 爱情 / 剧情 / 青春 / 普通话 / 婚姻 / 友谊 / 亲情 / 励志 / 怀旧 / 当代 / 纯爱 / 网络大电影 / 创业 / 奋斗 / 内地', '大陆中国', '高清中字', '豆瓣：2.6 ', '那是一个远离城市的山村，那是一个离现在说近不近说远不远的年代，两个青梅竹马豆蔻年化的少男少女，男孩有着伟大抱负满腔热血的想到外面的世界寻找幸福生活的希望，女孩无耐留守山村守望这那份最初的爱！\r\n毕业前夕林达和初心发生了争吵，林达只留下一封信便离开了山村，初心每天去两人一起看日出的山腰，期望能有林达的消息，父母却突然决定将初心许配邻村的傻子，初心不能接受，独自一人离开了山村去城里寻找林达。');
INSERT INTO `video`(`md5key`, `type`, `name`, `href`, `update_time`, `image_src`, `hdtag`, `director`, `casts`, `style`, `nation`, `update_status`, `score`, `summary`) VALUES ('eb4c50bf0bb4d9ee6456e39f3a6753f3', '1', '乱世后宫：红发美人', 'http://www.q2002.com/show/45853.html', ' ', 'http://wx4.sinaimg.cn/mw690/80df6fe6gy1fuuowr8nzhj205006kt9c.jpg', '高清中字', '郭勐', '郭勐', '华语 / 爱情 / 战争 / 传记 / 普通话 / 文艺 / 古代 / 古装 / 网络大电影 / 内地', '大陆中国', '高清中字', '豆瓣：2.6 ', '东周末年，战乱灾荒交加，总有异象天降。弃儿出生时，天降血雨，而且一出生浑身都是红毛。其父芮司徒只能愚昧地认为这个红毛孩儿是个不吉祥的怪物，要拿剑砍杀了，在司徒夫人的苦苦哀求下，才答应丢弃到河里。没想到，君太后伯姬到河边放祈福河灯，意外地捡到了这个毛孩儿……');
